          <Form.Control
            type='email'
            placeholder='Enter email'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId='password'>
          <Form.Label>Password</Form.Label>
          <Form.Control
            type='password'
            placeholder='Enter password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Button type='submit' variant='primary'>
          Sign In
        </Button>
      </Form>


    </ FormContainer> */}
    <div className="container">
    {error && <Message variant='danger'>{error}</Message>}
      {loading && <Loader />}
  <div className="left">
    <div className="header">
      <h2 className="animation a1">Welcome Back</h2>
      <h4 className="animation a2">Log in to your account using email and password</h4>
    </div>
    <form className="form" onSubmit={submitHandler}>
      <input type="email" className="form-field animation a3" placeholder="Email Address" value={email}
            onChange={(e) => setEmail(e.target.value)}/>
      <input type="password" className="form-field animation a4" placeholder="Password" value={password}
            onChange={(e) => setPassword(e.target.value)}/>
      <p className="animation a5"><a href="#">Forgot Password</a></p>
      <button type='submit'  className="animation a6">LOGIN</button>
    </form>
  </div>
  <div class="right"></div>
</div>

    </motion.div>
  )
}

export default LoginScreen
